PRG001_A78E:
	JSR Object_InteractWithWorld	 	; Move, detect, interact with blocks of world
	JSR Object_ShakeAndDrawMirrored	 ; Draw mirrored sprite
	JSR Object_DeleteOffScreen 		; Delete object if it falls off screen
