	ORA #SND_LEVELRISE
	STA Sound_QLevel1

PRG001_A8D5:
	BGE PRG001_A93D	 ; If timer is >= $2D, jump to PRG001_A93D

	; ObjSplash_DisTimer = 8
	LDA #$08
