	STA Map_Objects_Y,Y	 ; -> Map object Y

	LDA Map_Object_ActX,Y
	ADD #$08	; +8
	AND #$f0	; Align to grid
	STA Map_Objects_XLo,Y	 ; -> Map object X

	LDA Map_Object_ActXH,Y
	ADC #$00	 ; Apply carry
	STA Map_Objects_XHi,Y	 ; -> Map object X Hi

