	STA <Temp_Var1		; Store this into Temp_Var1
	INY		 	; Y++
	LDA [Map_Tile_AddrL],Y	; Get the next tile
	AND #$c0	 	; Only keep its upper 2 bits ($00, $40, $80, $C0)
	STA <Temp_Var2		; Store this into Temp_Var2

