	LDA [Map_Tile_AddrL],Y	; Get this tile
	AND #$c0	 	; Only keep its upper 2 bits ($00, $40, $80, $C0)
	STA <Temp_Var3		; Store this into Temp_Var3
	INY		 	; Y++
	LDA [Map_Tile_AddrL],Y	; Get this tile
	AND #$c0	 	; Only keep its upper 2 bits ($00, $40, $80, $C0)
	STA <Temp_Var4		; Store this into Temp_Var4

